contracts/protocol/InitialRealEstateOffe.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { CountersUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import { BitMapsUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/structs/BitMapsUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import { IRealEstateERC1155 } from "../interfaces/IRealEstateERC1155.sol";
import { IRealEstateReserves } from "../interfaces/IRealEstateReserves.sol";
import { IROFinance } from "../libraries/IROFinance.sol";

/// @title Initial Real Estate Offering (IRO)
/// @author Horizon DAO (Yuri Fernandes)
/// @notice Used to run IROs, mint tokens to RealEstateNFT
///     and distribute funds
contract InitialRealEstateOffering is OwnableUpgradeable, UUPSUpgradeable {
    using CountersUpgradeable for CountersUpgradeable.Counter;
    using BitMapsUpgradeable for BitMapsUpgradeable.BitMap;
    using IROFinance for IROFinance.Finance;

    /// @dev IRO status enum
    enum Status {
        PENDING,
        ONGOING,
        SUCCESS,
        FAIL
    }

    /// @dev Initial Real Estate Offerring structure
    /// @dev Since timestamps are 64 bit integers, last IRO
    ///     should finish at 21 de julho de 2554 11:34:33.709 UTC.
    struct IRO {
        address listingOwner;
        uint64 start;
        uint16 treasuryFee;
        uint16 listingOwnerFee;
        uint16 listingOwnerShare;
        uint64 end;
        uint256 softCap;
        uint256 hardCap;
        uint256 unitPrice;
        uint256 totalFunding;
    }

    struct WhitelistedCurrency {
        bool whitelisted;
        address[] relativePath;
    }

    /// @notice Treasury contract address
    address public treasury;

    /// @notice RealEstateNFT contract address
    IRealEstateERC1155 public realEstateNft;

    /// @notice RealEstateReserves contract address
    IRealEstateReserves public realEstateReserves;

    /// @notice Structure composed by the addresses
    ///     of contracts responsible for making financial operations
    IROFinance.Finance public finance;

    /// @dev Next available IRO ID
    CountersUpgradeable.Counter private _nextAvailableId;

    /// @dev mapping (iroId => iro)
    mapping(uint256 => IRO) private _iros;

    /// @dev mapping (iroId => user => commit)
    mapping(uint256 => mapping(address => uint256)) public commits;

    /// @dev mapping (iroId => realEstateId)
    mapping(uint256 => uint256) public realEstateId;

    /// @dev mapping (currencyAddress => WhitelistedCurrency)
    mapping(address => WhitelistedCurrency) public whitelistedCurrency;

    /// @dev Points out whether funds have been withdrawn from IRO
    BitMapsUpgradeable.BitMap private _fundsWithdrawn;

    /// @dev Points out whether the listingOwner has claimed it's share
    BitMapsUpgradeable.BitMap private _listingOwnerClaimed;

    /// @dev Whether an ID has already been set in the RealEstateNFT contract for the IRO
    BitMapsUpgradeable.BitMap private _realEstateIdSet;

    /// @dev Emitted when a new IRO is created
    event CreateIRO(
        uint256 indexed _id,
        address indexed _listingOwner,
        uint256 _unitPrice,
        uint16 _listingOwnerShare,
        uint16 _treasuryFee,
        uint64 _start,
        uint64 _end
    );

    /// @dev Emitted when a new Commit is made to an IRO
    event Commit(
        uint256 indexed _iroId,
        address indexed _user,
        address indexed _currency,
        uint256 _amountInBase,
        uint256 _purchasedTokens
    );

    /// @dev Emitted when `whitelistCurrency` is executed
    event WhitelistCurrency(address indexed _by, address indexed _currency, bool indexed _whitelist);

    /// @dev Emitted when tokens are claimed by investors
    event TokensClaimed(uint256 indexed _iroId, address indexed _by, address indexed _to, uint256 _amount);

    /// @dev Emitted when the listing owner claims it's shares of the tokens
    event OwnerTokensClaimed(uint256 indexed _iroId, address indexed _by, address indexed _to, uint256 _amount);

    /// @dev Emitted when an investors withdraw it's funds after an IRO fails
    event CashBack(uint256 indexed _iroId, address indexed _by, address indexed _to, uint256 _commitAmount);

    /// @dev Emitted when a new base currency is set
    event SetBaseCurrency(address indexed _by, address indexed _baseCurrency);

    /// @dev Emitted when the currency relative path is updated
    event PathUpdated(address indexed _by, address indexed _currency);

    event SetTreasury(address indexed _by, address indexed _treasury);

    event SetRealEstateReserves(address indexed _by, address indexed _realEstateReserves);

    /// @dev Emitted when funds from an IRO are withdrawn
    event FundsWithdrawn(
        uint256 indexed _iroId,
        address indexed _by,
        bool indexed _realEstateFundsSet,
        uint256 _listingOwnerAmount,
        uint256 _treasuryAmount,
        uint256 _realEstateFundsAmount
    );

    /// @dev Initialize IRO contract
    /// @param _realEstateNft RealEstateNFT contract address
    /// @param _treasury Treasury contract address
    /// @param _realEstateReserves RealEstateReserves contract address
    /// @param _baseCurrency Base token used to precify the IRO tokens
    /// @param _priceFeedRegistry Chainlink Price Feed Registry address
    /// @param _swapRouter Uniswap or Sushiswap swap router
    /// @param _weth WETH contract address
    function initialize(
        address _owner,
        address _realEstateNft,
        address _treasury,
        address _realEstateReserves,
        address _baseCurrency,
        address _priceFeedRegistry,
        address _swapRouter,
        address _weth
    ) external initializer {
        require(_realEstateNft != address(0), "!_realEstateNft");
        require(_treasury != address(0), "!_treasury");
        require(_baseCurrency != address(0), "!_baseCurrency");
        require(_priceFeedRegistry != address(0), "!_priceFeedRegistry");
        require(_swapRouter != address(0), "!_swapRouter");
        require(_weth != address(0), "!_weth");
        realEstateNft = IRealEstateERC1155(_realEstateNft);
        treasury = _treasury;
        realEstateReserves = IRealEstateReserves(_realEstateReserves);
        finance.initializeFinance(_swapRouter, _priceFeedRegistry, _weth, _baseCurrency);
        whitelistedCurrency[address(0)].whitelisted = true;
        _transferOwnership(_owner);
    }

    /// @dev Set a new base price token
    /// @param _baseCurrency Base price token address (ERC20)
    function setBaseCurrency(address _baseCurrency) external onlyOwner {
        require(_baseCurrency != address(0), "!_baseCurrency");
        finance.baseCurrency = _baseCurrency;
        emit SetBaseCurrency(msg.sender, _baseCurrency);
    }

    /// @dev Set new treasury
    /// @param _treasury Treasury address
    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "!_treasury");
        treasury = _treasury;
        emit SetTreasury(msg.sender, _treasury);
    }

    /// @dev Set new real estate reserves
    /// @param _realEstateReserves RealEstateReseres address
    function setRealEstateReserves(address _realEstateReserves) external onlyOwner {
        require(_realEstateReserves != address(0), "!_realEstateReserves");
        realEstateReserves = IRealEstateReserves(_realEstateReserves);
        emit SetRealEstateReserves(msg.sender, _realEstateReserves);
    }

    /// @dev Create new IRO
    /// @param _listingOwner Listing owner address
    /// @param _listingOwnerFee Listing owner fee in basis points
    /// @param _listingOwnerShare Listing owner share of IRO tokens in basis points
    /// @param _treasuryFee Treasury fee percentage in basis points
    /// @param _duration Duration of the IRO in seconds
    /// @param _softCap Minimum fundraising in base price token
    /// @param _hardCap Maximum fundraising in base price token
    /// @param _unitPrice Price per unit of IRO token in base price token
    /// @param _startOffset Time before IRO begins
    function createIRO(
        address _listingOwner,
        uint16 _listingOwnerFee,
        uint16 _listingOwnerShare,
        uint16 _treasuryFee,
        uint64 _duration,
        uint256 _softCap,
        uint256 _hardCap,
        uint256 _unitPrice,
        uint64 _startOffset
    ) external onlyOwner {
        require(_listingOwnerShare <= IROFinance.SHARE_DENOMINATOR, "Invalid owner share");
        require(_treasuryFee <= IROFinance.FEE_DENOMINATOR, "Invalid treasury fee");
        require((_hardCap - _softCap) % _unitPrice == 0, "Caps should be multiples of unitPrice");

        uint256 currentId = iroLength();
        uint64 start_ = now64() + _startOffset;
        uint64 end_ = start_ + _duration;
        _iros[currentId] = IRO({
            listingOwner: _listingOwner,
            start: start_,
            treasuryFee: _treasuryFee,
            listingOwnerFee: _listingOwnerFee,
            listingOwnerShare: _listingOwnerShare,
            end: end_,
            softCap: _softCap,
            hardCap: _hardCap,
            unitPrice: _unitPrice,
            totalFunding: 0
        });
        _nextAvailableId.increment();

        emit CreateIRO(currentId, _listingOwner, _unitPrice, _listingOwnerShare, _treasuryFee, start_, end_);
    }

    /// @notice Commit to an IRO
    /// @param _iroId ID of the IRO
    /// @param _currency Currency address
    /// @param _amountToPay Expected amount to pay (without slippage)
    /// @param _amountToPurchase Amount of IRO tokens to purchase
    /// @param _slippage Slippage in basis points when swapping token by
    ///     base currency (not applicable when paying directly with it)
    function commit(
        uint256 _iroId,
        address _currency,
        uint256 _amountToPay,
        uint256 _amountToPurchase,
        uint16 _slippage
    ) external payable {
        require(_amountToPurchase > 0, "_amountToPurchase should be greater than zero");
        WhitelistedCurrency memory whitelistedCurrency_ = whitelistedCurrency[_currency];
        require(_currency == finance.baseCurrency || whitelistedCurrency_.whitelisted, "Currency not allowed");
        require(_slippage <= IROFinance.SLIPPAGE_DENOMINATOR, "Invalid _slippage");
        IRO memory iro = getIRO(_iroId);
        require(_getStatus(iro) == Status.ONGOING, "IRO is not active");
        require(iro.totalFunding + _amountToPurchase * iro.unitPrice <= iro.hardCap, "Hardcap reached");
        if (_currency != address(0) && msg.value > 0) {
            IROFinance.sendEther(msg.sender, msg.value);
        }

        uint256 valueInBase = finance.processPayment(
            iro.unitPrice,
            _currency,
            _amountToPay,
            _amountToPurchase,
            _slippage,
            whitelistedCurrency_.relativePath
        );

        commits[_iroId][msg.sender] += _amountToPurchase;
        _iros[_iroId].totalFunding += valueInBase;

        emit Commit(_iroId, msg.sender, _currency, valueInBase, _amountToPurchase);
    }

    /// @dev Enable receiving ETH
    receive() external payable {}

    /// @notice Claim purchased tokens when IRO successful or
    ///     get back commit amount in base currency if IRO failed
    /// @param _iroId ID of the IRO
    /// @param _to Address to send the claimed tokens
    function claim(uint256 _iroId, address _to) external {
        IRO memory iro = getIRO(_iroId);
        Status status = _getStatus(iro);
        require(status > Status.ONGOING, "IRO not finished");
        uint256 commitAmount = commits[_iroId][msg.sender];
        require(commitAmount > 0, "Nothing to mint");
        if (status == Status.SUCCESS) {
            uint256 amountToMint = commitAmount / iro.unitPrice;
            realEstateNft.mint(_retrieveRealEstateId(_iroId), msg.sender, amountToMint);
            emit TokensClaimed(_iroId, msg.sender, _to, amountToMint);
        } else {
            IROFinance.sendErc20(_to, commitAmount, finance.baseCurrency);
            commits[_iroId][msg.sender] = 0;
            emit CashBack(_iroId, msg.sender, _to, commitAmount);
        }
    }

    /// @notice Claim listing owner tokens
    /// @param _iroId ID of the IRO
    /// @param _to Address to send the tokens
    function listingOwnerClaim(uint256 _iroId, address _to) external {
        IRO memory iro = getIRO(_iroId);
        require(msg.sender == iro.listingOwner, "!allowed");
        require(!_listingOwnerClaimed.get(_iroId), "Already claimed");
        require(_getStatus(iro) == Status.SUCCESS, "IRO not successful");
        require(iro.listingOwnerShare > 0, "Nothing to claim");
        uint256 listingOwnerAmount = IROFinance.shareToAmount(iro.totalFunding, iro.unitPrice, iro.listingOwnerShare);
        realEstateNft.mint(_retrieveRealEstateId(_iroId), _to, listingOwnerAmount);
        _listingOwnerClaimed.set(_iroId);
        emit OwnerTokensClaimed(_iroId, msg.sender, _to, listingOwnerAmount);
    }

    /// @notice Withdraw and distribute funds from successful IROs
    /// @param _iroId ID of the IRO
    function withdraw(uint256 _iroId) external {
        IRO memory iro = getIRO(_iroId);
        require(_getStatus(iro) == Status.SUCCESS, "IRO not successful");
        require(!_fundsWithdrawn.get(_iroId), "Already withdrawn");
        (
            uint256 listingOwnerAmount,
            uint256 treasuryAmount,
            uint256 realEstateReservesAmount,
            bool realEstateReservesSet
        ) = finance.distributeFunds(
                iro.listingOwner,
                treasury,
                realEstateReserves,
                _retrieveRealEstateId(_iroId),
                iro.totalFunding,
                iro.listingOwnerFee,
                iro.treasuryFee
            );
        _fundsWithdrawn.set(_iroId);
        emit FundsWithdrawn(
            _iroId,
            msg.sender,
            realEstateReservesSet,
            listingOwnerAmount,
            treasuryAmount,
            realEstateReservesAmount
        );
    }

    /// @dev Whitelist currencies
    /// @param _currency Currency ERC20 address
    /// @param _whitelist Whether to whitelist
    function whitelistCurrency(address _currency, bool _whitelist) external onlyOwner {
        require(_currency != address(0), "!invalid address");
        whitelistedCurrency[_currency].whitelisted = _whitelist;
        emit WhitelistCurrency(msg.sender, _currency, _whitelist);
    }

    /// @dev Update relative swap router path of a currency
    /// @param _currency Currency ERC20 address
    /// @param _relativePath Swap relative path
    function setrelativePath(address _currency, address[] memory _relativePath) external onlyOwner {
        require(_currency != address(0), "!invalid address");
        whitelistedCurrency[_currency].relativePath = _relativePath;
        emit PathUpdated(msg.sender, _currency);
    }

    /// @notice Get the expected price of an IRO purchase (without slippage)
    /// @notice Takes into account swap fees
    /// @param _iroId ID of the IRO
    /// @param _currency Currency address
    /// @param _amountToPurchase Amount of IRO tokens to purchase
    function expectedPrice(
        uint256 _iroId,
        address _currency,
        uint256 _amountToPurchase
    ) external view returns (uint256) {
        WhitelistedCurrency memory whitelistedCurrency_ = whitelistedCurrency[_currency];
        require(whitelistedCurrency_.whitelisted, "Currency is not available");
        IRO memory iro = getIRO(_iroId);
        return
            finance.expectedPrice(
                iro.unitPrice,
                _currency,
                _amountToPurchase,
                whitelistedCurrency_.relativePath.length + 2
            );
    }

    /// @notice Get the price with slippage
    /// @notice Takes into account swap fees
    /// @param _iroId ID of the IRO
    /// @param _currency Currency address
    /// @param _amountToPurchase Amount of IRO tokens to purchase
    /// @param _slippage Swap slippage in basis points
    function priceWithSlippage(
        uint256 _iroId,
        address _currency,
        uint256 _amountToPurchase,
        uint16 _slippage
    ) external view returns (uint256) {
        WhitelistedCurrency memory whitelistedCurrency_ = whitelistedCurrency[_currency];
        require(whitelistedCurrency_.whitelisted, "Currency is not available");
        IRO memory iro = getIRO(_iroId);
        return
            finance.priceWithSlippage(
                iro.unitPrice,
                _currency,
                _amountToPurchase,
                _slippage,
                whitelistedCurrency_.relativePath.length + 2
            );
    }

    /// @notice Get the amount of remaining IRO tokens
    /// @param _iroId ID of the IRO
    function remainingTokens(uint256 _iroId) external view returns (uint256) {
        IRO memory iro = getIRO(_iroId);
        return (iro.hardCap - iro.totalFunding) / iro.unitPrice;
    }

    /// @notice Get IRO status
    /// @param _iroId ID of the IRO
    function getStatus(uint256 _iroId) external view returns (Status) {
        IRO memory iro = _iros[_iroId];
        return _getStatus(iro);
    }

    /// @notice Check slippage denominator
    function slippageDenominator() external pure returns (uint16) {
        return IROFinance.SLIPPAGE_DENOMINATOR;
    }

    /// @notice Check fee denominator
    function feeDenominator() external pure returns (uint16) {
        return IROFinance.FEE_DENOMINATOR;
    }

    /// @notice Check share denominator
    function shareDenominator() external pure returns (uint16) {
        return IROFinance.SHARE_DENOMINATOR;
    }

    /// @notice Get IRO
    /// @param _iroId ID of the IRO
    function getIRO(uint256 _iroId) public view returns (IRO memory) {
        require(_iroId < iroLength(), "_iroId out-of-bounds");
        return _iros[_iroId];
    }

    /// @notice Get current time (uint64)
    function now64() public view returns (uint64) {
        return uint64(block.timestamp);
    }

    /// @notice Get total amount of IROs
    function iroLength() public view returns (uint256) {
        return _nextAvailableId.current();
    }

    /// @dev Retrieve the realEstateId associated with a given IRO
    /// @dev If none is assigned, assigns a new one
    /// @param _iroId ID of the IRO
    function _retrieveRealEstateId(uint256 _iroId) internal returns (uint256 _realEstateId) {
        if (!_realEstateIdSet.get(_iroId)) {
            _realEstateId = realEstateNft.nextRealEstateId();
            realEstateId[_iroId] = _realEstateId;
            _realEstateIdSet.set(_iroId);
        } else {
            _realEstateId = realEstateId[_iroId];
        }
    }

    /// @inheritdoc UUPSUpgradeable
    function _authorizeUpgrade(address newImplementation) internal virtual override onlyOwner {}

    /// @dev Get status of an IRO
    /// @param _iro IRO structure
    function _getStatus(IRO memory _iro) internal view returns (Status) {
        if (now64() <= _iro.start) return Status.PENDING;
        if (now64() < _iro.end) {
            if (_iro.totalFunding == _iro.hardCap) return Status.SUCCESS;
            return Status.ONGOING;
        }
        if (now64() >= _iro.end) {
            if (_iro.totalFunding < _iro.softCap) return Status.FAIL;
            return Status.SUCCESS;
        }
        return Status.FAIL;
    }
}

contracts/protocol/PriceOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";

import { AggregatorV3Interface } from "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";

import { IERC20Extended } from "../interfaces/IERC20Extended.sol";
import { IPriceOracle } from "../interfaces/IPriceOracle.sol";

/// @title Price Oracle
/// @author Horizon DAO (Yuri Fernandes)
/// @notice Uses Chainlink Price Aggregators to retrieve base price in quote (base/quote)
/// @dev Aggregator registration conventions:
///		- For stablecoins 1:1 with USD, register base/USD tokens as base/stablecoin priceAggregator
///		- For ETH use zero address
contract PriceOracle is IPriceOracle, Ownable {
    /// @dev mapping (base => quote => priceAggregator)
    mapping(address => mapping(address => AggregatorV3Interface)) public priceAggregator;

    /// @dev Emitted when a new price aggregator is set
    event SetAggregator(address indexed _by, address indexed _base, address indexed _quote, address _aggregator);

    /// @dev Instantiate PriceOracle
    /// @param _owner Owner address
    constructor(address _owner) {
        _transferOwnership(_owner);
    }

    /// @inheritdoc IPriceOracle
    function setAggregator(address _base, address _quote, address _aggregator) external override onlyOwner {
        priceAggregator[_base][_quote] = AggregatorV3Interface(_aggregator);
        emit SetAggregator(msg.sender, _base, _quote, _aggregator);
    }

    /// @inheritdoc IPriceOracle
    function getPrice(address _base, address _quote) external view override returns (uint256 basePrice) {
        basePrice = uint256(_getAnswer(_base, _quote));
        uint8 priceDecimals = _getPriceDecimals(_base, _quote);
        uint8 quoteDecimals = _getTokenDecimals(_quote);
        if (priceDecimals > quoteDecimals) {
            basePrice /= 10 ** (priceDecimals - quoteDecimals);
        } else if (priceDecimals < quoteDecimals) {
            basePrice *= 10 ** (priceDecimals - quoteDecimals);
        }
    }

    /// @dev Get answer (price) given `_base` and `_quote`
    /// @param _base Base currency address
    /// @param _quote Quote currency address
    /// @return Base price in quote (int256)
    function _getAnswer(address _base, address _quote) internal view returns (int256) {
        AggregatorV3Interface priceAggregator_ = priceAggregator[_base][_quote];
        require(address(priceAggregator_) != address(0), "Price Aggregator not available");
        (uint256 roundId, int256 priceInBase, , uint256 updatedAt, uint256 answeredInRound) = priceAggregator_
            .latestRoundData();
        require(roundId == answeredInRound, "Invalid Answer");
        require(updatedAt > 0, "Round not complete");
        return priceInBase;
    }

    /// @dev Get price decimals
    /// @param _base Base currency address
    /// @param _quote Quote currency address
    /// @return Number of decimals in price response
    function _getPriceDecimals(address _base, address _quote) internal view returns (uint8) {
        return priceAggregator[_base][_quote].decimals();
    }

    /// @dev Get token decimals
    /// @param _token Token address
    /// @return Number of decimals in `_token`
    function _getTokenDecimals(address _token) internal view returns (uint8) {
        return IERC20Extended(_token).decimals();
    }
}

contracts/protocol/Vesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { IERC165 } from "@openzeppelin/contracts/interfaces/IERC165.sol";
import { IERC20 } from "@openzeppelin/contracts/interfaces/IERC20.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Counters } from "@openzeppelin/contracts/utils/Counters.sol";
import { IVoteEscrow } from "../interfaces/IVoteEscrow.sol";

/// @title Vesting
/// @dev Used to vest underlying ERC20 for various addresses
/// @author HorizonDAO (Yuri Fernandes)
contract Vesting is Ownable {
    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;

    /// @dev Multiplier used during division operations to decrease rounding errors
    uint256 public constant BASE_MULTIPLIER = 1e18;

    /// @dev Minimum vesting period
    uint256 public constant MIN_VESTING_PERIOD = 365 days; // 1 year

    /// @dev Maximum vesting period
    uint256 public constant MAX_VESTING_PERIOD = 4 * 365 days; // 4 years

    /// @dev Minimum lock period after claiming vested tokens (if lockVested is false, locking is not required)
    uint256 public constant LOCK_VESTED_MIN_PERIOD = 365 days; // 1 year

    /// @dev Maximum lock period after claiming vested tokens
    uint256 public constant LOCK_VESTED_MAX_PERIOD = 4 * 365 days; // 4 years

    /// @dev Position structure containing required data:
    ///     - beneficiary: Who'll have control over vested tokens
    ///     - amount: Amount of tokens to be vested in the position
    ///     - amountPaid: Amount of vested tokens claimed
    ///     - vestingStart: When tokens start to vest
    ///     - vestingEnd: When tokens vest completely
    ///     - lockVested: Whether to enforce token locking after vested
    struct Position {
        address beneficiary;
        uint256 amount;
        uint256 amountPaid;
        uint256 vestingStart;
        uint256 vestingEnd;
        bool lockVested;
    }

    /// @dev Underlying vested token address
    address public immutable underlying;

    /// @dev VoteEscrow address, where tokens will be locked
    address public voteEscrow;

    /// @dev Amount of tokens been vested
    uint256 public totalVesting;

    /// @dev ID of the next vested position ID
    Counters.Counter private _currentPositionId;

    /// @dev How many positions vested (all tokens claimed)
    Counters.Counter private _vestedPositions;

    /// @dev Array containing all vesting positions
    Position[] public positions;

    /// @dev mapping (user => vestingPostions)
    /// @dev Contains the user vesting positions' indexes
    mapping(address => uint256[]) public userPositionIndexes;

    /// @dev Emitted when a new Vote Escrow contract is set
    event VoteEscrowSet(address indexed _admin, address _voteEscrow);

    /// @dev Emitted when a new vested position is created
    event PositionCreated(
        address indexed _admin,
        address indexed _beneficiary,
        uint256 indexed _positionId,
        uint256 _amount,
        uint256 _vestingStart,
        uint256 _vestingEnd,
        bool _lockVested
    );

    /// @dev Emitted when an amount of vested tokens is claimed
    event AmountClaimed(address indexed _by, address indexed _recipient, uint256 _amount, uint256 _voteLockPeriod);

    /// @dev Initialize Vesting contract
    /// @param _underlying Address of the underlying vesting asset
    constructor(address _underlying) {
        require(
            IERC165(_underlying).supportsInterface(type(IERC20).interfaceId),
            "Underlying should be IERC20 compatible"
        );
        underlying = _underlying;
    }

    /// @dev Set a vote escrow contract
    /// @param _voteEscrow Address of the vote escrow contract
    function setVoteEscrow(address _voteEscrow) external onlyOwner {
        voteEscrow = _voteEscrow;
        emit VoteEscrowSet(_msgSender(), _voteEscrow);
    }

    /// @dev Create a new vesting position
    /// @param _beneficiary The address of the vesting position owner
    /// @param _amount Amount of underlying to be vested
    /// @param _cliffPeriod Period of time that tokens won't vest
    /// @param _vestingDuration Amount of time tokens will vest
    /// @param _lockVested Whether vested underlying locking will be enforced
    function createPosition(
        address _beneficiary,
        uint256 _amount,
        uint256 _cliffPeriod,
        uint256 _vestingDuration,
        bool _lockVested
    ) external onlyOwner {
        require(_beneficiary != address(0), "Invalid beneficiary");
        require(
            _vestingDuration >= MIN_VESTING_PERIOD && _vestingDuration <= MAX_VESTING_PERIOD,
            "Invalid vesting duration"
        );
        require(usableSupply() >= _amount, "Insufficient underlying");
        uint256 vestingStart = block.timestamp + _cliffPeriod;
        uint256 vestingEnd = vestingStart + _vestingDuration;
        positions.push(
            Position({
                beneficiary: _beneficiary,
                amount: _amount,
                amountPaid: 0,
                vestingStart: vestingStart,
                vestingEnd: vestingEnd,
                lockVested: _lockVested
            })
        );
        uint256 currentPositionId = _currentPositionId.current();
        userPositionIndexes[_beneficiary].push(currentPositionId);
        _currentPositionId.increment();
        totalVesting += _amount;

        emit PositionCreated(
            _msgSender(),
            _beneficiary,
            currentPositionId,
            _amount,
            vestingStart,
            vestingEnd,
            _lockVested
        );
    }

    /// @notice Claim vested underlying
    /// @param _positionId ID of the position to claim vested tokens
    /// @param _recipient Recipient of the vested tokens
    /// @param _lockVestedPeriod Amount of time to lock vested tokens (mandatory if lockVested is true)
    function claim(uint256 _positionId, address _recipient, uint256 _lockVestedPeriod) external {
        Position memory userPosition = positions[_positionId];
        require(userPosition.beneficiary == _msgSender(), "Invalid position");
        require(userPosition.vestingStart >= block.timestamp, "Vesting hasn't started");
        require(_recipient != address(0), "Invalid recipient");
        (uint256 amountDue_, uint256 prevAmountPaid_) = _amountDuePaid(userPosition);
        if (amountDue_ == 0) return;
        if (userPosition.lockVested || _lockVestedPeriod > 0) {
            require(voteEscrow != address(0), "No vote escrow");
            require(
                _lockVestedPeriod >= MIN_VESTING_PERIOD && _lockVestedPeriod <= MAX_VESTING_PERIOD,
                "Invalid lock time"
            );
            IERC20(underlying).safeApprove(voteEscrow, amountDue_);
            IVoteEscrow(voteEscrow).lock(_recipient, amountDue_, _lockVestedPeriod);
        } else {
            IERC20(underlying).safeTransfer(_recipient, amountDue_);
        }
        positions[_positionId].amountPaid += amountDue_;
        totalVesting -= amountDue_;

        if (prevAmountPaid_ + amountDue_ == 0) _vestedPositions.increment();

        emit AmountClaimed(_msgSender(), _recipient, amountDue_, _lockVestedPeriod);
    }

    /// @notice Size of the positions array (how many vested positions exist)
    function vestedPositions() external view returns (uint256) {
        return _currentPositionId.current();
    }

    /// @notice How many position still contain vesting tokens
    function activeVestedPositions() external view returns (uint256) {
        return _currentPositionId.current() - _vestedPositions.current();
    }

    /// @notice Amount of underlying tokens Vesting contract owns
    function totalSupply() public view returns (uint256) {
        return IERC20(underlying).balanceOf(address(this));
    }

    /// @notice Amount of tokens available to be vested
    function usableSupply() public view returns (uint256) {
        return totalSupply() - totalVesting;
    }

    /// @notice Amount of vested tokens for a specific position
    /// @param _positionId ID of the position
    /// @return Claimable amount
    function amountDue(uint256 _positionId) public view returns (uint256) {
        Position memory userPosition = positions[_positionId];
        (uint256 amountDue_, ) = _amountDuePaid(userPosition);
        return amountDue_;
    }

    /// @notice Amount of vested tokens paid for a specific position
    /// @param _positionId ID of the position
    /// @return Paid amount
    function amountPaid(uint256 _positionId) public view returns (uint256) {
        Position memory userPosition = positions[_positionId];
        (, uint256 amountPaid_) = _amountDuePaid(userPosition);
        return amountPaid_;
    }

    /// @notice Total amount of vested underlying for an user
    /// @param _account User address
    /// @return Total vested amount
    function userTotalAmountDue(address _account) external view returns (uint256) {
        uint256[] memory userPositionIndexes_ = userPositionIndexes[_account];
        uint256 totalAmountDue;
        uint256 amountDue_;
        for (uint256 i = 0; i < userPositionIndexes_.length; i++) {
            (amountDue_, ) = _amountDuePaid(positions[userPositionIndexes_[i]]);
            totalAmountDue += amountDue_;
        }
        return totalAmountDue;
    }

    /// @notice Returns the position indexes for a given user (comma-separated)
    /// @param _account User address
    /// @return Comma-separated user positions' indexes
    function getUserPositionIndexes(address _account) external view returns (string memory) {
        uint256[] memory userPositionIndexes_ = userPositionIndexes[_account];
        bytes memory positionIndexes = "";
        for (uint256 i = 0; i < userPositionIndexes_.length; i++) {
            positionIndexes = abi.encodePacked(positionIndexes);
            if (i != userPositionIndexes_.length) positionIndexes = abi.encodePacked(positionIndexes, ", ");
        }
        return string(positionIndexes);
    }

    /// @dev Calculates the amount of vested tokens due for a given position and the amount already paid
    /// @param _position Position instance
    /// @return amountDue_ Claimable amount
    /// @return amountPaid_ Paid amount
    function _amountDuePaid(Position memory _position) internal view returns (uint256 amountDue_, uint256 amountPaid_) {
        amountPaid_ = _position.amountPaid;
        amountDue_ =
            ((((
                block.timestamp < _position.vestingEnd ? block.timestamp : _position.vestingEnd - _position.vestingStart
            ) * _position.amount) * BASE_MULTIPLIER) / (_position.vestingEnd - _position.vestingStart)) /
            BASE_MULTIPLIER -
            amountPaid_;
    }
}

contracts/token/ERC20Permit.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { IERC165 } from "@openzeppelin/contracts/interfaces/IERC165.sol";
import { IERC20 } from "@openzeppelin/contracts/interfaces/IERC20.sol";
import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { Counters } from "@openzeppelin/contracts/utils/Counters.sol";

/// @dev EIP-2612 (Permit extension for ERC20)
abstract contract ERC20Permit is ERC20, IERC165 {
    using Counters for Counters.Counter;

    /// @dev Typehash for the domain separator
    /// @dev keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)")
    bytes32 public constant EIP712_DOMAIN_TYPEHASH = 0x8b73c3c69bb8fe3d512ecc4cf759cc79239f7b179b0ffacaa9a75d522b39400f;

    /// @dev Typehash for the permit function
    /// @dev keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)")
    bytes32 public constant PERMIT_TYPEHASH = 0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;

    /// @dev mapping (account => permitNonce)
    /// @dev Used to avoid repeated permit usage
    mapping(address => Counters.Counter) private _nonces;

    /// @dev EIP-712 domain separator
    bytes32 public immutable DOMAIN_SEPARATOR;

    /// @dev Initialize ERC20Permit contract
    /// @param _name Token name
    /// @param _symbol Token symbol
    constructor(string memory _name, string memory _symbol) ERC20(_name, _symbol) {
        // set domain separator
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(EIP712_DOMAIN_TYPEHASH, keccak256(bytes(_name)), keccak256("1"), block.chainid, address(this))
        );
    }

    /// @notice Sets `_value` as the allowance of `_spender` over the owners's tokens.
    /// @notice Anyone with a signed permit message hash by the `_owner`, can execute this function.
    /// @param _owner Token owner address
    /// @param _spender Spender address
    /// @param _value Amount to give allowance to `_spender`
    /// @param _deadline EIP-712 signed message validity
    /// @param _signature EIP-712 signature
    function permit(
        address _owner,
        address _spender,
        uint256 _value,
        uint256 _deadline,
        bytes memory _signature
    ) external {
        require(block.timestamp <= _deadline, "ERC20Permit: deadline reached");

        uint256 nonce = _nonces[_owner].current();
        bytes32 permitHash = ECDSA.toTypedDataHash(
            DOMAIN_SEPARATOR,
            keccak256(abi.encode(PERMIT_TYPEHASH, _owner, _spender, _value, nonce, _deadline))
        );

        require(ECDSA.recover(permitHash, _signature) == _owner, "ERC20Permit: invalid permit");

        _nonces[_owner].increment();
        _approve(_owner, _spender, _value);
    }

    /// @dev Current valid nonce for an user
    /// @param _owner User address
    function nonces(address _owner) external view returns (uint256) {
        return _nonces[_owner].current();
    }

    /// @inheritdoc IERC165
    function supportsInterface(bytes4 interfaceId) external pure virtual returns (bool) {
        return type(IERC165).interfaceId == interfaceId || type(IERC20).interfaceId == interfaceId;
    }
}

contracts/token/PioneerERC1155.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import { MerkleProof } from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import { Base64 } from "@openzeppelin/contracts/utils/Base64.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { RoyalERC1155 } from "./RoyalERC1155.sol";

/// @title HorizonDAO Pioneer NFT
/// @author Yuri Fernandes (HorizonDAO)
/// @notice NFTs owned by HorizonDAO pioneer members
/// @notice Holding these NFTs will accrue into various rewards
///     during HorizonDAO development, such as, for example Airdrops
contract PioneerERC1155 is RoyalERC1155 {
    using Strings for uint256;

    /// @dev Pioneer types
    enum Pioneer {
        BRONZE,
        SILVER,
        GOLD
    }

    /// @dev Total number of tokens that can be purchased
    uint256 public constant PURCHASABLE_SUPPLY = 10446;

    /// @dev How many tokens can be purchased during Whitelisted sale
    uint256 public constant WHITELIST_MAX_PURCHASES = 1000;

    /// @dev How many tokens whitelisted users can purchase
    uint256 public constant WHITELIST_PURCHASE_PER_ADDRESS = 10;

    /// @dev Maximum amount of tokens users can get from private claim
    uint256 public constant PRIVATE_MAX_CLAIMS = 4;

    /// @dev Total claimable amount of tokens through airdrops
    uint256 public constant AIRDROP_MAX_CLAIMS = 100;

    /// @dev How many Gold Pioneers a user can get from private claim
    uint256 public constant PRIVATE_CLAIM_GOLD = 1;

    /// @dev Represents 100% chance, there will be 3 Pioneer tokens
    ///     with decreasing chances to be minted during purchases
    ///     the total chances should sum to MAX_CHANCE
    uint256 public constant MAX_CHANCE = 1_000;

    /// @dev Unit prie of tokens for Whitelisted sale
    uint256 public immutable whitelistTokenUnitPrice;

    /// @dev Unit price of tokens for Public sale
    uint256 public immutable publicTokenUnitPrice;

    /// @dev Amount of NFTs that have been purchased so far
    uint256 public purchasedAmount;

    /// @dev Calculated pseudo-random number should fall in range to acquire a certain Pioneer
    mapping(Pioneer => uint256) public thresholds;

    /// @dev Private Merkle Root (can be set once)
    bytes32 public privateMerkleRoot;

    /// @dev Whitelist Merkle Root (can be set once)
    bytes32 public whitelistMerkleRoot;

    /// @dev Airdrop Merkle Root (can be set multiple times)
    bytes32 public airdropMerkleRoot;

    /// @dev How many tokens users have claimed during private sale
    mapping(address => bool) public userPrivateClaimed;

    /// @dev How many tokens the whitelisted users have claimed
    mapping(address => uint256) public userWhitelistPurchasedAmount;

    /// @dev Which Airdrop an user has participated
    mapping(address => uint256) public userAirdropNonce;

    /// @dev Nonce of the current airdrop Merkle Tree
    uint256 public airdropNonce;

    /// @dev Amount of Airdrop tokens claimed
    uint256 public airdropClaimed;

    /// @dev When the Public sale will begin (if zero, means sale has not been initialized)
    uint256 public publicSaleStartTime;

    /// @dev Emitted when Private Merkle Root is set
    event PrivateMerkleRootSet(address indexed _admin, bytes32 _root);

    /// @dev Emitted when Sale is initialized
    event SaleInitialized(address indexed _admin, bytes32 _whitelistMerkleRoot, uint256 _publicSaleStartTime);

    /// @dev Emitted when the Airdrop Merkle Root is set
    event AirdropMerkleRootSet(address indexed _admin, uint256 indexed _airdropNonce, bytes32 _root);

    /// @dev Emitted when a new base image URI is set for the tokens
    event NewImageUri(address indexed _admin, string _uri);

    /// @dev Emitted when ethers are withdrawn from the contract
    event Withdrawal(address indexed _admin, address indexed _to, uint256 _amount);

    /// @dev Emitted when a PioneerNFT is purchased
    event PioneerClaim(address indexed _by, Pioneer indexed _pioneer, uint256 _unitPrice, uint256 _amount);

    /// @dev Emitted when an amount of Pioneer NFTs are claimed
    event PrivateClaim(address indexed _by);

    /// @dev Emitted when tokens are purchased via whitelistPurchase function
    event WhitelistPurchase(address indexed _by, uint256 _amount);

    /// @dev Emitted when tokens are purchased via publicPurchase function
    event PublicPurchase(address indexed _by, uint256 _amount);

    /// @dev Emitted when
    event AirdropClaim(address indexed _by, uint256 _amount);

    /// @dev constructor to initialize PioneerPromoERC1155 contract
    /// @param _imageUri Base image URI
    /// @param _admin Adminstrative address, can execute various configuration related functions
    /// @param _owner Should be an EOA, will have rights over OpenSea collection configuration
    /// @param _publicTokenUnitPrice Price per token for Public Sale
    /// @param _whitelistTokenUnitPrice Price per token for Whitelisted Sale
    /// @param _chances Array with the chances of getting a Pioneer for each token
    constructor(
        string memory _imageUri,
        address _admin,
        address _owner,
        uint256 _publicTokenUnitPrice,
        uint256 _whitelistTokenUnitPrice,
        uint256[3] memory _chances
    ) RoyalERC1155(_imageUri, _admin, _owner) {
        require(_admin != address(0), "Admin should not be ZERO ADDRESS");
        require(_whitelistTokenUnitPrice < _publicTokenUnitPrice, "No discount applied");
        for (uint8 i = 0; i < _chances.length; i++) {
            if (i > 0) {
                require(_chances[i - 1] >= _chances[i], "Invalid _chance array");
                thresholds[Pioneer(i)] += thresholds[Pioneer(i - 1)];
            }
            thresholds[Pioneer(i)] += _chances[i];
        }
        require(thresholds[Pioneer.GOLD] == MAX_CHANCE, "_chances sum should be MAX_CHANCE");
        publicTokenUnitPrice = _publicTokenUnitPrice;
        whitelistTokenUnitPrice = _whitelistTokenUnitPrice;
        emit NewImageUri(_msgSender(), _imageUri);
    }

    /// @notice Returns the Base64 encoded metadata for a given token
    /// @param _id Token ID
    /// @return Base64 encoded metadata
    function uri(uint256 _id) public view override returns (string memory) {
        require(_id <= uint256(Pioneer.GOLD), "Invalid token ID");
        return string(abi.encodePacked("data:application/json;base64,", Base64.encode(bytes(metadata(_id)))));
    }

    /// @notice Returns the stringified metadata JSON for a given token
    /// @param _id Token ID
    /// @return Stringified metadata JSON
    function metadata(uint256 _id) public view returns (string memory) {
        return
            string(
                abi.encodePacked(
                    '{"name":"',
                    name(_id),
                    '","description":"',
                    description(_id),
                    '","image":"',
                    imageURI(_id),
                    '"}'
                )
            );
    }

    /// @notice Get token name
    /// @param _id Token ID
    /// @return Token name
    function name(uint256 _id) public pure returns (string memory) {
        require(_id <= uint256(Pioneer.GOLD), "Invalid token ID");
        if (_id == uint256(Pioneer.BRONZE)) return "Bronze Horizon Pioneer Badge";
        if (_id == uint256(Pioneer.SILVER)) return "Silver Horizon Pioneer Badge";
        return "Gold Horizon Pioneer Badge";
    }

    /// @notice Get token description
    /// @param _id Token ID
    /// @return Token description
    function description(uint256 _id) public pure returns (string memory) {
        require(_id <= uint256(Pioneer.GOLD), "Invalid token ID");
        if (_id == uint256(Pioneer.BRONZE)) return "";
        if (_id == uint256(Pioneer.SILVER)) return "";
        return "";
    }

    /// @notice Get the image URI for a given token
    /// @param _id Token ID
    /// @return Image URI
    function imageURI(uint256 _id) public view returns (string memory) {
        string memory uri_ = super.uri(uint256(0));
        require(keccak256(bytes(uri_)) != keccak256(""), "!baseURI");
        return string(abi.encodePacked(super.uri(uint256(0)), _id.toString()));
    }

    /// @notice Whether the sale has been initialized
    function saleInitialized() public view returns (bool) {
        return whitelistMerkleRoot != bytes32(0);
    }

    /// @notice Whether public sale started
    function publicSaleStarted() public view returns (bool) {
        return saleInitialized() && block.timestamp >= publicSaleStartTime;
    }

    /// @dev Initialize sale (first whitelisted address, then public sale begins)
    /// @param _whitelistMerkleRoot Whitelist Merkle Root
    /// @param _publicSaleOffset Amount of time that will take for the Public sale to begin
    function initializeSale(bytes32 _whitelistMerkleRoot, uint256 _publicSaleOffset) external onlyAdmin {
        require(!saleInitialized(), "Merkle root already set");
        require(_whitelistMerkleRoot != bytes32(0), "Invalid Merkle root");
        whitelistMerkleRoot = _whitelistMerkleRoot;
        publicSaleStartTime = block.timestamp + _publicSaleOffset;
        emit SaleInitialized(_msgSender(), _whitelistMerkleRoot, block.timestamp + _publicSaleOffset);
    }

    /// @dev Set Private claiming merkle root (only once)
    /// @param _root Private Merkle Root
    function setPrivateRoot(bytes32 _root) external onlyAdmin {
        require(privateMerkleRoot == bytes32(0), "Merkle root already set");
        privateMerkleRoot = _root;
        emit PrivateMerkleRootSet(_msgSender(), _root);
    }

    /// @dev Set Airdrop merkle root (multiple times)
    /// @param _root Airdrop Merkle Root
    function setAirdropRoot(bytes32 _root) external onlyAdmin {
        require(airdropClaimed < AIRDROP_MAX_CLAIMS, "!airdrop");
        airdropMerkleRoot = _root;
        emit AirdropMerkleRootSet(_msgSender(), ++airdropNonce, _root);
    }

    /// @dev Set new base image URI for the tokens
    /// @param _uri Base image URI
    function setImageBaseURI(string memory _uri) external onlyAdmin {
        _setURI(_uri);
        emit NewImageUri(_msgSender(), _uri);
    }

    /// @notice Claim private whitelisted tokens
    /// @param _proof Private Merkle Proof
    function privateClaim(bytes32[] memory _proof) external {
        require(MerkleProof.verify(_proof, privateMerkleRoot, keccak256(abi.encodePacked(_msgSender()))), "!root");
        require(!userPrivateClaimed[_msgSender()], "claimed");
        _mint(_msgSender(), uint256(Pioneer.GOLD), PRIVATE_CLAIM_GOLD, bytes(""));
        userPrivateClaimed[_msgSender()] = true;
        emit PrivateClaim(_msgSender());
    }

    /// @notice Purchase whilelisted tokens (maximum amount: WHITELIST_PURCHASE_PER_ADDRESS)
    /// @param _amount Amount to purchase
    /// @param _proof Whitelist Merkle Proof
    function whitelistPurchase(uint256 _amount, bytes32[] memory _proof) external payable {
        require(whitelistMerkleRoot != bytes32(0), "!initialized");
        require(MerkleProof.verify(_proof, whitelistMerkleRoot, keccak256(abi.encodePacked(_msgSender()))), "!root");
        require(purchasedAmount + _amount <= PURCHASABLE_SUPPLY, "!purchase");
        require(
            userWhitelistPurchasedAmount[_msgSender()] + _amount <= WHITELIST_PURCHASE_PER_ADDRESS,
            "Maximum amount purchased"
        );
        _processPurchaseRequest(whitelistTokenUnitPrice, _amount);
        userWhitelistPurchasedAmount[_msgSender()] += _amount;
        purchasedAmount += _amount;
        emit WhitelistPurchase(_msgSender(), _amount);
    }

    /// @notice Purchase Pioneer NFTs randomly from the 3 type of tokens (Bronze, Silver and Gold)
    /// @param _amount Amount to purchase
    function publicPurchase(uint256 _amount) external payable {
        require(publicSaleStarted(), "!start");
        require(purchasedAmount + _amount <= PURCHASABLE_SUPPLY, "!purchase");
        _processPurchaseRequest(publicTokenUnitPrice, _amount);
        purchasedAmount += _amount;
        emit PublicPurchase(_msgSender(), _amount);
    }

    /// @notice Claim airdrop
    /// @param _amount Amount to claim
    /// @param _proof Airdrop Merkle Proof
    function claimAirdrop(uint256 _amount, bytes32[] memory _proof) external {
        require(
            MerkleProof.verify(_proof, airdropMerkleRoot, keccak256(abi.encodePacked(_msgSender(), _amount))),
            "!merkleRoot"
        );
        require(airdropClaimed + _amount <= AIRDROP_MAX_CLAIMS, "!airdrop");
        uint256 airdropNonce_ = airdropNonce;
        require(userAirdropNonce[_msgSender()] < airdropNonce_, "!userNonce");
        _processPurchaseRequest(0, _amount);
        airdropClaimed += _amount;
        userAirdropNonce[_msgSender()] = airdropNonce_;
        emit AirdropClaim(_msgSender(), _amount);
    }

    /// @dev Withdraw all ethers from contract
    /// @param _to Address to send the funds
    function withdraw(address _to) external onlyAdmin {
        uint256 etherBalance = address(this).balance;
        require(etherBalance > 0, "No ethers to withdraw");
        _sendValue(_to, etherBalance);
        emit Withdrawal(_msgSender(), _to, etherBalance);
    }

    /// @dev Utility function to send an amount of ethers to a given address
    /// @param _to Address to send ethers
    /// @param _amount Amount of ethers to send
    function _sendValue(address _to, uint256 _amount) internal {
        (bool success, ) = _to.call{ value: _amount }("");
        require(success, "Failed sending ethers");
    }

    /// @dev Process purchase payment and minting
    /// @param _tokenUnitPrice Unit price of the token
    /// @param _amount Amount of tokens to buy
    function _processPurchaseRequest(uint256 _tokenUnitPrice, uint256 _amount) internal {
        uint256 totalPrice = _amount * _tokenUnitPrice;
        require(msg.value >= totalPrice, "Not enough ethers");
        uint256 magicValue = uint256(
            keccak256(abi.encodePacked(_msgSender(), block.timestamp, _amount, purchasedAmount))
        );

        uint256 chance;
        uint256[3] memory amounts;
        for (uint256 i = 0; i < _amount; i++) {
            chance = magicValue % MAX_CHANCE;
            if (chance < thresholds[Pioneer.BRONZE]) ++amounts[0];
            else if (chance < thresholds[Pioneer.SILVER]) ++amounts[1];
            else ++amounts[2];
            magicValue = uint256(keccak256(abi.encodePacked(magicValue / MAX_CHANCE)));
        }

        for (uint8 i = 0; i < amounts.length; i++) {
            if (amounts[i] == 0) continue;
            _mint(_msgSender(), i, amounts[i], bytes(""));
            emit PioneerClaim(_msgSender(), Pioneer(i), _tokenUnitPrice, amounts[i]);
        }
        uint256 surplus = msg.value - totalPrice;
        if (surplus > 0) _sendValue(_msgSender(), surplus);
    }
}

